actions.script:
/* 
Get the interaction ID
*/

let inputType;
let interaction;

if(input.trigger == "manual.spark" && input.data._type == "interaction") {
    inputType = "manualInteraction";
    interaction = input.item._id;

} else if (input.trigger == "content.create" && input.data._type == "interaction") {
    inputType = "autoInteraction";
    interaction = input.item;

} else {
    inputType = "unknown";
}

input = {};
input.inputType = inputType;
input.interaction = interaction;

return done(null, input);

/*
Extract the data we need.
*/

// Get interaction info
let interaction = {
    "_id": input.interaction._id,
    "definition": input.interaction.definition,
    "realm": {
        "title": input.interaction.realms[0].title,
        "shortTitle": input.interaction.realms[0].data.shortTitle,
        "email": input.interaction.realms[0].data.registrarEmail
    }
};

// Get contact info
let primaryContact;
let secondaryContacts = {};

if(!input.interaction.rawData.hasOwnProperty("contact")) {
    // If no contacts attached to this interaction, stop the Reaction
    return done(null, 'STOP');
}

for(let i=0; i<input.interaction.rawData.contact.length; i++) {
    if(input.interaction.rawData.contact[i].email == input.interaction.primaryEmail) {

        primaryContact = {
            "_id": input.interaction.data.contact[i],
            "firstName": input.interaction.rawData.contact[i].firstName,
            "lastName": input.interaction.rawData.contact[i].lastName,
            "email": input.interaction.rawData.contact[i].email
        };

    } else {

        secondaryContacts[input.interaction.data.contact[i]] = {
            "_id": input.interaction.data.contact[i],
            "firstName": input.interaction.rawData.contact[i].firstName,
            "lastName": input.interaction.rawData.contact[i].lastName,
            "email": input.interaction.rawData.contact[i].email
        };
    }
}

if(secondaryContacts.length == 0) { // No secondary contacts. This Reaction is not needed.
    return done(null, 'STOP');
}

input = {};

input.interaction = interaction;
input.primaryContact = primaryContact;
input.secondaryContacts = secondaryContacts;

return done(null, input);

/*
Get tickets related to these contacts and this interaction
*/

//Load packages
var _ = require('lodash');
var async = require('async');

// Set up results structures to return info to the next step]
var tickets = [];

// Get input data needed
let interactionID = _.get(input, 'interaction._id');

/*
GET https://api.fluro.io/tickets/:connection/:connectionID
DESCRIPTION
Returns an array of all tickets connected to a specified content item.

Options are 
/tickets/interaction/<INTERACTIONID>
/tickets/contact/<CONTACTID>
/tickets/event/<EVENTID>
*/
$fluro.api.get(`/tickets/interaction/${interactionID}`)
    .then(res => {
        tickets.push(res.data);
    })
    .catch(err => {
        return done(err, "STOP");
    });

// Return results
input.tickets = tickets;

return done(null, input);

/*
Put tickets under each contact
*/

let interaction = input.interaction;
let primaryContact = input.primaryContact;
let secondaryContacts = input.secondaryContacts;
let tickets = input.tickets[0];

// Put tickets under each contact
for (let i=0; i<tickets.length; i++) {

    if(tickets[i].status == "active" && tickets[i].hasOwnProperty("event")) { // If the ticket is active and there is an associated event

        if(secondaryContacts.hasOwnProperty(tickets[i].contact._id)) { // If the contact is a secondary contact

            // If the contact does not already have a ticket array, create it
            if(!secondaryContacts[tickets[i].contact._id].hasOwnProperty("tickets")) { 
                secondaryContacts[tickets[i].contact._id].tickets = [];
            }

            // Add the ticket to the contact's ticket array
            secondaryContacts[tickets[i].contact._id].tickets.push({
                 "_id": tickets[i]._id,
                "title": tickets[i].title,
                "event": {
                    "_id": tickets[i].event._id,
                    "title": tickets[i].event.title,
                    "startDate": tickets[i].event.startDate,
                    "endDate": tickets[i].event.endDate
                }
            });
        }
    }
}


// Return the data
input = {};

input.interaction = interaction;
input.primaryContact = primaryContact;
input.secondaryContacts = secondaryContacts;

return done(null, input);

/*
Email each extra attendee with ticket details
*/


//Load packages
var _ = require('lodash');
var async = require('async');

// Set up request headers
const headers = {"Content-Type": "application/json; charset=utf-8"};

// Set up results structures to return info to the next step]
const results = [];

// Get input data needed
let interaction = _.get(input, 'interaction');
let primaryContact = _.get(input, 'primaryContact');
let secondaryContacts = _.get(input, 'secondaryContacts');

let ticketURL = `http://tickets.fluro.io/interaction/${interaction._id}`;
let interactionURL = `https://app.fluro.io/list/interaction/${interaction.definition}/${interaction._id}/edit`;

// Filter out Contacts with no tickets
let secondaryContactIDs = Object.keys(secondaryContacts);
let secondaryContactsWithTickets = {};
for(let i=0; i<secondaryContactIDs.length; i++) {
    if(secondaryContacts[secondaryContactIDs[i]].hasOwnProperty("tickets")) {
        secondaryContactsWithTickets[secondaryContactIDs[i]] = secondaryContacts[secondaryContactIDs[i]];
    }
}

// Run the async function
return async.forEachOfSeries(secondaryContactsWithTickets, emailTicketInfo, emailTicketInfoCallback);

// Function to execute on each contact
function emailTicketInfo(contact, index, next) {

    let eventList = "";

    for(let i=0; i<contact.tickets.length; i++) {

        let startDateObject = new Date(contact.tickets[i].event.startDate);
        let startDateString = startDateObject.toLocaleString('en-AU', {dateStyle: "long", timeStyle: "short", timeZone: "Australia/Perth"});

        eventList = eventList + `<h3>${contact.tickets[i].event.title}</h3>
            <p>${contact.tickets[i].title}</p>
            <p>${startDateString}</p>`;
    }

    let emailBody = `
    <div style="text-align: center;">
        <h1>${interaction.realm.shortTitle} check-in details</h1>
        <hr/>
        <img src="https://api.fluro.io/system/qr?input=${ticketURL}" style="border: 10px solid black; border-radius: 10px;" width="150px"/>
        <h3>${contact.firstName} ${contact.lastName}</h3>
        <p>${contact.email}</p>
        <hr/>
        ${eventList}
        <hr/>
    </div>
    <div style="text-align: left;">
        <p>The payment receipt and full registration details have been sent to ${primaryContact.firstName} ${primaryContact.lastName} (${primaryContact.email}).</p>
        <h4>Need help?</h4>
        <p>Reply to this email, or contact us at <a href="mailto:${interaction.realm.email}?subject=${interaction.realm.title} enquiry">${interaction.realm.email}.</p>
        <hr/>
        <p><small>Christian Conventions of Western Australia<br/>
        ABN 38 223 904 915<br/></small></p>
        <p style="color:#AAA;"><small>
        Admin links: <a href="${interactionURL}">Form submission</a></small></p>
    </div>`;

    let body = {
    	"fromName": interaction.realm.title,
    	"fromEmail": interaction.realm.email,
    	"toEmails": contact.email,
    	//"toContactIDs": [""],
    	"subject": `${interaction.realm.shortTitle} check-in details`,
    	"body": emailBody
    };

    let emailReactionID = "621dfa141b17ad02986c7d78";

    // POST https://api.fluro.io/reaction/spark/:reactionID
    $fluro.api.post(`/reaction/spark/${emailReactionID}`,body,headers)
    .then(res => {
        results.push(res.data);
        next();
    })
    .catch(err => next(err));
}

// Callback function â€” after all iterations are finished
function emailTicketInfoCallback(err) {
    if (err) {
        var errorMessage = $fluro.utils.errorMessage(err);
        return done(errorMessage, "STOP");
    }

	// Return results
	return done(null, input);
}
