actions.script:
/*
Get the interaction ID
*/

let inputType;
let interaction;

if (input.trigger === 'manual.spark' && input.data._type === 'interaction') {
    inputType = 'manualInteraction';
    interaction = input.item._id;
} else if (input.trigger === 'content.create' && input.data._type === 'interaction') {
    inputType = 'autoInteraction';
    interaction = input.item;
} else {
    inputType = 'unknown';
}

// Replace input with only the data we need
input = {
    inputType,
    interaction
};

return done(null, input);

/*
Extract the data we need.
*/

// Load packages
const has = require('lodash/has');

// Get interaction info
const { interaction } = input;
const interactionRealm = interaction.realms[0];

const formattedInteraction = {
    _id: interaction._id,
    definition: interaction.definition,
    realm: {
        title: interactionRealm.title,
        shortTitle: interactionRealm.data.shortTitle,
        email: interactionRealm.data.registrarEmail
    }
};

// Get contact info
let primaryContact;
const secondaryContacts = {};

if (!has(interaction.rawData, 'contact')) {
    // If no contacts attached to this interaction, stop the Reaction
    return done(null, 'STOP');
}

// Sanitises email addresses
// - puts to lower case
// - strips leading and trailing whitespace
function sanitiseEmail(email) {
    return email.toLowerCase().trim();
}

// Loop through all the contacts
for (let i = 0; i < interaction.rawData.contact.length; i += 1) {
    const thisContactID = interaction.data.contact[i];
    const thisContactData = interaction.rawData.contact[i];

    // If the current contact has an email address, see if it matchces the primary contact
    if (has(thisContactData, 'email')) {
        // If primary contact, set primary contact data
        if (sanitiseEmail(thisContactData.email) === sanitiseEmail(interaction.primaryEmail)) {
            primaryContact = {
                _id: thisContactID,
                firstName: thisContactData.firstName,
                lastName: thisContactData.lastName,
                email: thisContactData.email
            };
        // If secondary contact, add to secondary contacts array
        } else {
            secondaryContacts[thisContactID] = {
                _id: thisContactID,
                firstName: thisContactData.firstName,
                lastName: thisContactData.lastName,
                email: thisContactData.email
            };
        }
    }
}

// If no secondary contacts with email addresses
if (Object.keys(secondaryContacts).length === 0) {
    // Stop the Reaction, because it is not needed
    return done(null, 'STOP');
}

input = {
    interaction: formattedInteraction,
    primaryContact,
    secondaryContacts
};

return done(null, input);

/*
Get tickets related to this interaction
*/

// Set up results structures to return info to the next step]
const tickets = [];

// Get input data needed
const interactionID = input.interaction._id;

/*
GET https://api.fluro.io/tickets/:connection/:connectionID
DESCRIPTION
Returns an array of all tickets connected to a specified content item.

Options are
/tickets/interaction/<INTERACTIONID>
/tickets/contact/<CONTACTID>
/tickets/event/<EVENTID>
*/
$fluro.api.get(`/tickets/interaction/${interactionID}`)
    .then((res) => {
        for (let i = 0; i < res.data.length; i += 1) {
            tickets.push(res.data[i]);
        }
    })
    .catch((err) => done(err, 'STOP'));

// Return results
input.tickets = tickets;

return done(null, input);

/*
Put tickets under each contact
*/

// Load packages
const has = require('lodash/has');

const {
    interaction,
    primaryContact,
    secondaryContacts,
    tickets
} = input;

// If no tickets, stop the Reaction
if (tickets.length < 1) {
    return done(null, 'STOP');
}

// Put tickets under each contact
for (let i = 0; i < tickets.length; i += 1) {
    // If the ticket is active and there is an associated event
    if (tickets[i].status === 'active' && has(tickets[i], 'event')) {
        // If the contact is a secondary contact
        if (has(secondaryContacts, tickets[i].contact._id)) {
            // If the contact does not already have a ticket array, create it
            if (!has(secondaryContacts[tickets[i].contact._id], 'tickets')) {
                secondaryContacts[tickets[i].contact._id].tickets = [];
            }

            // Add the ticket to the contact's ticket array
            secondaryContacts[tickets[i].contact._id].tickets.push({
                _id: tickets[i]._id,
                title: tickets[i].title,
                event: {
                    _id: tickets[i].event._id,
                    title: tickets[i].event.title,
                    startDate: tickets[i].event.startDate,
                    endDate: tickets[i].event.endDate
                }
            });
        }
    }
}

// Return the data
input = {
    interaction,
    primaryContact,
    secondaryContacts
};

return done(null, input);

/*
Email each extra attendee with ticket details
*/

// Load packages
const has = require('lodash/has');
const forEachOfSeries = require('async/forEachOfSeries');

// Set up request headers
const headers = { 'Content-Type': 'application/json; charset=utf-8' };

// Set up results structures to return info to the next step]
const results = [];

// Get input data needed
const { interaction, primaryContact, secondaryContacts } = input;

const ticketURL = `http://tickets.fluro.io/interaction/${interaction._id}`;
const interactionURL = `https://app.fluro.io/list/interaction/${interaction.definition}/${interaction._id}/edit`;

// Filter out Contacts with no tickets
const secondaryContactIDs = Object.keys(secondaryContacts);
const secondaryContactsWithTickets = {};

for (let i = 0; i < secondaryContactIDs.length; i += 1) {
    if (has(secondaryContacts[secondaryContactIDs[i]], 'tickets')) {
        secondaryContactsWithTickets[
            secondaryContactIDs[i]
        ] = secondaryContacts[
            secondaryContactIDs[i]
        ];
    }
}

// Function to execute on each contact
function emailTicketInfo(contact, index, next) {
    let eventList = '';

    for (let i = 0; i < contact.tickets.length; i += 1) {
        const startDateObject = new Date(contact.tickets[i].event.startDate);
        const startDateString = startDateObject.toLocaleString('en-AU', {
            dateStyle: 'long',
            timeStyle: 'short',
            timeZone: 'Australia/Perth'
        });

        eventList = `
            ${eventList}
            <h3>${contact.tickets[i].event.title}</h3>
            <p>${contact.tickets[i].title}</p>
            <p>${startDateString}</p>`;
    }

    const emailBody = `
        <div style='text-align: center;'>
            <h1>${interaction.realm.shortTitle} check-in details</h1>
            <hr/>
            <img src='https://api.fluro.io/system/qr?input=${ticketURL}' style='border: 10px solid black; border-radius: 10px;' width='150px'/>
            <h3>${contact.firstName} ${contact.lastName}</h3>
            <p>${contact.email}</p>
            <hr/>
            ${eventList}
            <hr/>
        </div>
        <div style='text-align: left;'>
            <p>The payment receipt and full registration details have been sent to ${primaryContact.firstName} ${primaryContact.lastName} (${primaryContact.email}).</p>
            <h4>Need help?</h4>
            <p>Reply to this email, or contact us at <a href='mailto:${interaction.realm.email}?subject=${interaction.realm.title} enquiry'>${interaction.realm.email}.</p>
            <hr/>
            <p><small>Christian Conventions of Western Australia<br/>
            ABN 38 223 904 915<br/></small></p>
            <p style='color:#AAA;'><small>
            Admin links: <a href='${interactionURL}'>Form submission</a></small></p>
        </div>`;

    const body = {
        fromName: interaction.realm.title,
        fromEmail: interaction.realm.email,
        toEmails: contact.email,
        subject: `${interaction.realm.shortTitle} check-in details`,
        body: emailBody
    };

    const emailReactionID = '621dfa141b17ad02986c7d78';

    // POST https://api.fluro.io/reaction/spark/:reactionID
    $fluro.api.post(`/reaction/spark/${emailReactionID}`, body, headers)
        .then((res) => {
            results.push(res.data);
            next();
        })
        .catch((err) => next(err));
}

// Callback function â€” after all iterations are finished
function emailTicketInfoCallback(err) {
    if (err) {
        const errorMessage = $fluro.utils.errorMessage(err);
        return done(errorMessage, 'STOP');
    }

    // Return results
    return done(null, input);
}

// Run the async function
return forEachOfSeries(
    secondaryContactsWithTickets,
    emailTicketInfo,
    emailTicketInfoCallback
);
