/*
This Reaction automatically sends a link to the 
Square Invoice to the first contact whenever a form is 
filled out.
*/

return done(null, input);

var inputType;

if(input.trigger == "manual.spark" && input.data["_type"] == "interaction") {
    inputType = "manualInteraction";
} else if (input.trigger == "content.create" && input.data["_type"] == "interaction") {
    inputType = "autoInteraction";
} else {
    // Unknown input. Stop the Reaction
    return done(null, 'STOP');
}


input.inputType = inputType;

// Finish the action and send the input to the next action
return done(null, input);

// Get and format data for Manual Spark in an Interaction

// Check that a Transaction and a Contact are attached to this Interaction.
var transactionExists = input.item.hasOwnProperty('transactions');
var contactExists = input.item.hasOwnProperty('contacts');

// If there's no transaction or contact, stop the Reaction
if(transactionExists === false || contactExists === false) {
    return done(null, 'STOP');
} 

// Find all the data needed for the next steps
var inputType = input.inputType;
var formTitle = input.item.title;
var formRealm = input.item.realms[0];
var receiptUrl = input.item.transactions[0].transactionData.payment.receiptUrl;
var statementDescription = input.item.transactions[0].transactionData.payment.cardDetails.statementDescription;
var interactionLink = 'https://app.fluro.io/list/interaction/' + input.item.definition + '/' + input.item["_id"] + '/edit';
var transactionLink = 'https://app.fluro.io/list/transaction/' + input.item.transactions[0]["_id"] + '/edit';
var contact = input.item.contacts[0];

// Format input nicely for next steps
input = {};
input.formTitle = formTitle;
input.formRealm = formRealm;
input.receiptUrl = receiptUrl;
input.statementDescription = statementDescription;
input.interactionLink = interactionLink;
input.transactionLink = transactionLink;
input.contact = contact;
input.inputType = inputType;

// Finish the action and send the input to the next action
return done(null, input);

// Get and format data for automatic spark on form submission

// Check that a Transaction and a Contact are attached to this Interaction.
var transactionExists = input.data.submittedData.hasOwnProperty('transactions');
var contactExists = input.data.submittedData.hasOwnProperty('contacts');

// If there's no transaction or contact, stop the Reaction
if(transactionExists === false || contactExists === false) {
    return done(null, 'STOP');
} 

// Find all the data needed for the next steps
var inputType = input.inputType;
var formTitle = input.data.submittedData.title;
var formRealm = input.data.submittedData.realms[0];
var receiptUrl = input.data.submittedData.transaction.transactionData.payment.receiptUrl;
var statementDescription = input.data.submittedData.transaction.transactionData.payment.cardDetails.statementDescription;
var interactionLink = 'https://app.fluro.io/list/interaction/' + input.data.definition + '/' + input.item + '/edit';
var transactionLink = 'https://app.fluro.io/list/transaction/' + input.data.submittedData.transaction["_id"] + '/edit';
var contact = input.data.submittedData.contacts[0];

// Format input nicely for next steps
input = {};
input.formTitle = formTitle;
input.formRealm = formRealm;
input.receiptUrl = receiptUrl;
input.statementDescription = statementDescription;
input.interactionLink = interactionLink;
input.transactionLink = transactionLink;
input.contact = contact;
input.inputType = inputType;

// Finish the action and send the input to the next action
return done(null, input);

/* Check if the Realm is a Convention or something else.
If a convention, set the contact email address to the one in the Convention definition
Else set it to the bookkeeper's email address */

var contactEmail;

if(input.formRealm.definition =="convention") {
    contactEmail = input.formRealm.data.registrarEmail;
} else {
    contactEmail = "bookkeeper@ccowa.org";
}

// Add contact email to input
delete input["formRealm"];
input.contactEmail = contactEmail;

// Finish the action and send the input to the next action
return done(null, input)

