/*
This Reaction re-subscribes all contacts to 
Allow Promotional Emails when they submit a form.
*/

return done(null, input);

/*
Format input based on what it is.
*/

// Create variables
var inputType;
var contacts;

// Check that this is an interaction with contacts attached, then check for trigger type
if (input.data._type == "interaction" && input.item.hasOwnProperty("contacts") && input.trigger == "content.create") {
    inputType = "autoInteraction";
    contacts = input.item.contacts;
} else if (input.data._type == "interaction" && input.item.hasOwnProperty("contacts") && input.trigger == "manual.spark") {
    inputType = "manualInteraction";
    contacts = input.item.contacts;
} else {
    inputType = "unknown";
}

// Return data
input = {};
input.inputType = inputType;
input.contacts = contacts;

return done(null, input);

/*
Re-subscribe people to Allow Promotional Emails
*/

// Get input into variables
var contacts = input.contacts;
var emailsToSubscribe = [];

// Set up request headers
const headers = {"Content-Type": "application/json; charset=utf-8"};

// Set up results objects
const results = [];

// Build array of emails to process. All the email addresses for a contact must be resubscribed for the Allow Promotional Emails setting to turn on.
for(let i=0; i<contacts.length; i++) {
    for(let j=0; j<contacts[i].emails.length; j++) {
        emailsToSubscribe.push(contacts[i].emails[j]);
    }
}

let body = { "emails": emailsToSubscribe };

return $fluro.api.post("/mailout/resubscribe", body, headers) 
.then(function(res) { 
    results.push(res.data);

    // Return results
    input.results = results;
    return done(null, input);
}, done);

